import React, { useState } from 'react'
import TableChanllenge from './table';

export default function ChallengeTwo(filterdata) {
    const [state, setState] = useState('');
    const data = [
        {
            id: 0,
            title: 'Arthur',
            vote: 12,
            data: '2019-01-24'
        },
        {
            id: 1,
            title: 'Arthur 1',
            vote: 14,
            data: '2019-01-24'
        },
        {
            id: 2,
            title: 'Arthur 2',
            vote: 22,
            data: '2019-03-24'
        },
        {
            id: 3,
            title: 'Arthur 3',
            vote: 124,
            data: '2019-05-24'
        },
        {
            id: 4,
            title: 'Arthur 4 ',
            vote: 112,
            data: '2021-01-24'
        },
        {
            id: 5,
            title: 'Arthur 5',
            vote: 1232,
            data: '2020-01-24'
        },
    ]
    const checkString = () => {

        if(state === 'VOTED'){
           filterdata = data.sort((a,b) => (a.vote < b.vote) ? 1 : -1);
        }else{
            filterdata = data.sort((a,b) => (a.date < b.date) ? 1 : -1);
        }
        return (
            <TableChanllenge
            data={filterdata}
            />
        )
    }
  return (
    <div>
        <div style={{marginBottom:'50px'}}>Challenge Two</div>
        <div>
            <button onClick={() => setState('VOTED')}>Most Upvoted</button>
            <button onClick={() => setState('RECENT')}>Most Recent</button>
        </div>
        {checkString()}
    </div>
  )
}
