import React, { useEffect, useState } from 'react'
import ContainerText from './container_text';

export default function ChallengeOne(dataClick) {

    const [state, setState] = useState(0);
    const data = [
        {
            id: 0,
            title: 'Monday',
            string: 'Monday....'
        },
        {
            id: 1,
            title: 'Tuesday',
            string: 'Tuesday.....'
        },
        {
            id: 2,
            title: 'Wednesday',
            string: 'Wednesday.....'
        },
        {
            id: 3,
            title: 'Thursday',
            string: 'Thursday.....'
        }
        ,
        {
            id: 4,
            title: 'Friday',
            string: 'Friday.....'
        },
        {
            id: 5,
            title: 'Saturday',
            string: 'Saturday.....'
        }
        ,
        {
            id: 6,
            title: 'Sunday',
            string: 'Sunday......'
        }
    ]

    const checkDay = () => {
        dataClick = data.find((x) => (x.id === state))
        return (
            <ContainerText 
            data = {dataClick}
            />
        )
    }

    useEffect(() => {
        checkDay();
    },[state])

    return (
        <div>
            <div>
                <button onClick={() => setState(0)}> Reset</button>
                <button onClick={() => setState(state - 1)}> Prev</button>
                <button onClick={() => setState(state + 1)}> Next</button>
                <p>{checkDay()}</p>
            </div>
        </div>
    )
}
