import React, { useEffect, useState } from 'react'

export default function ChallengeOne() {

    const [state, setState] = useState(0);
    const data = [
        {
            id: 0,
            title: 'Monday',
            string: 'Monday....'
        },
        {
            id: 1,
            title: 'Tuesday',
            string: 'Tuesday.....'
        },
        {
            id: 2,
            title: 'Wednesday',
            string: 'Wednesday.....'
        },
        {
            id: 3,
            title: 'Thursday',
            string: 'Thursday.....'
        }
        ,
        {
            id: 4,
            title: 'Friday',
            string: 'Friday.....'
        },
        {
            id: 5,
            title: 'Saturday',
            string: 'Saturday.....'
        }
        ,
        {
            id: 6,
            title: 'Sunday',
            string: 'Sunday......'
        }
    ]
    useEffect(() => {
        checkDay();
    })

    const checkDay = () => {
        const newdata = data.find((x) => x.id === state);
        console.log(newdata);
    }

    return (
        <div>
            <div>
                <button onClick={() => setState(0)}> Reset</button>
                <button onClick={() => setState(state - 1)}> Prev</button>
                <button onClick={() => setState(state + 1)}> Next</button>
                <p>{state}</p>
            </div>
        </div>
    )
}
